import React, { useRef, useState } from 'react';
import clickSound from './asset/click.wa';
import './App.css';

function App() {
  const [count, setCount] = useState(0);
  const audioRef = useRef(null);

  const playSound = () => {
    audioRef.current.currentTime = 0; 
    audioRef.current.play();
  };

  const ic = () => {
    playSound.play();
    setCount(count + 1);
  }
  const ic10 = () => {
    playSound.play();
    setCount(count + 10);
  }
  const dc = () => {
    playSound.play();
    setCount(count - 1);
  }
  const dc10 = () => {
    playSound.play();
    setCount(count - 10);
  }
  const rc = () => {
    playSound.play();
    setCount(0);
  }

  return (
    <div className='c'>

      <h1>Counters</h1>
      <span>{count}</span>
      <audio ref={audioRef} src={clickSound} />
        <div className='btng'>
        <button onClick={ic}  className='btn s1'>+</button>
        <button onClick={rc} className='btn s2'>â†»</button>
        <button onClick={dc} className='btn s1'>-</button>
        </div>

        <div className='btng'>
        <button onClick={ic10} className='btn s2'>+10</button>
        <button onClick={dc10} className='btn s2'>-10</button>
        </div>

    </div>
  );
}

export default App;
