import React, { useState } from "react";
import "./App.css";

function App() {
  const [count, setCount] = useState(0);

  return (
    <div className="container">
      <div className="card">
        <h1 className="title">Counters</h1>
        <h2 className="count">{count}</h2>

        <div className="btn-group">
          <button onClick={() => setCount(count + 1)} className="btn">+</button>

          <button onClick={() => setCount(0)} className="btn">‚ü≥</button>

          <button onClick={() => setCount(count - 1)} className="btn">-</button>
        </div>

        <div className="btn-group">
          <button onClick={() => setCount(count + 10)} className="btn pur">+10</button>

          <button onClick={() => setCount(count - 10)} className="btn purple">-10</button>
        </div>
      </div>
    </div>
  );
}

export default App;
